import React, { useState, useEffect } from 'react';
import { useContext } from 'react';
import { useSearchParams } from 'react-router-dom';
import TaskContext from '../../context/TaskContext';
import TokenContext from '../../context/TokenContext';
import axios from "../../Axios/axios.js"
import "./createTask.css"

function CreateTask() {
    const { tasks, dispatch } = useContext(TaskContext)
    const { userToken } = useContext(TokenContext)
    const [title, setTitle] = useState("")
    const [description, setDescription] = useState("")
    const [toast, setToast] = useState();
    const [searchParams] = useSearchParams();
    const isEditing = searchParams.get('isEditing') === 'true';
    const taskId = searchParams.get('id');

    useEffect(() => {
        if (isEditing && taskId) {
            const task = tasks[taskId];
            if (task) {
                setTitle(task.title);
                setDescription(task.description);
            }
        }
    }, [isEditing, taskId, tasks]);

    const handleAdd = async (e) => {
        e.preventDefault();
        try {
            if (isEditing) {
                const res = await axios.put(`/task/updateTask/${taskId}`, { title, description }, {
                    headers: {
                        Authorization: `Bearer ${userToken}`
                    }
                });
                dispatch({
                    type: "SET_EDIT_TASK",
                    id: parseInt(taskId),
                    payload: { title, description }
                });
                setToast(res.data.message || "Task updated successfully");
            } else {
                const res = await axios.post("/task/addTask", { title, description }, {
                    headers: {
                        Authorization: `Bearer ${userToken}`
                    }
                });
                dispatch({
                    type: "ADD_TASK",
                    title,
                    description
                });
                setToast(res.data.message);
            }
            showToast();
            if (isEditing) {
                window.location.href = '/';
            }
        } catch (error) {
            console.log(error);
            setToast(error.response?.data?.message || "Something went wrong");
            showToast();
        }
        setTitle("")
        setDescription("")
    }

    const showToast = () => {
        const toast = document.getElementById('toast');
        toast.style.display = "block"
        setTimeout(hideToast, 2000)
    }
    const hideToast = () => {
        const toast = document.getElementById('toast');
        toast.style.display = "none"
    }
    return (
        <div className="addContainer md:w-1/3 md:mx-auto mx-3 mt-3 flex justify-center">
            <div className='w-11/12'>
                <form onSubmit={handleAdd}>
                    <div>
                        <label htmlFor="title">Title</label>
                        <input
                            type="text"
                            name="title"
                            id="title"
                            value={title}
                            required
                            onChange={(e) => setTitle(e.target.value)}
                            className='bg-white border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-yellow-500 focus:border-yellow-500 block w-full p-2.5' />
                    </div>
                    <div className='my-3'>
                        <label htmlFor="description">Description</label>
                        <textarea
                            rows={5}
                            name="description"
                            id="description"
                            value={description}
                            required
                            onChange={(e) => setDescription(e.target.value)}
                            style={{ resize: "none" }}
                            className='bg-white border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-yellow-500 focus:border-yellow-500 block w-full p-2.5' />
                    </div>
                    <div className='flex justify-center'>
                        <button
                            type='submit'
                            className='bg-yellow-500 rounded-md text-white px-5 py-1'
                        >
                            {isEditing ? 'Edit Done' : 'Add'}
                        </button>
                    </div>
                </form>
                <div className="toast bg-yellow-500 text-white p-3 rounded-xl shadow-2xl text-center absolute bottom-4 left-1/2 -translate-x-1/2" id='toast'>
                    <p>{toast}</p>
                </div>
            </div>
        </div>
    );
}

export default CreateTask;
